% various weight functions for the Circle Splineclear all, close all, clcn = 40;u = (0 : n - 1)' / (n - 1);f = zeros(n, 4);% linearf(:, 1) = u;% parabolicf(u < 0.5, 2) = 2 * u(u < 0.5).^2;f(u >= 0.5, 2) = 1 - 2 * (u(u >= 0.5) - 1).^2;% cubicf(:, 3) = (3 - 2 * u).*u.^2;% trigonometricf(:, 4) = sin(u * pi / 2).^2;fh = figure('name','Weight functions');plot(u, f)xlabel('u')ylabel('f(u)')title('Weight functions')lh = legend(  'linear',  'parabolic',  'cubic',  'trigonometric',  'location', 'northwest'); %print -djpg weight-functions.jpg% Cubic function:% f(u) = a0 + a1 * u + a2 * u^2 + a3 * u^3% f'(u) = a1 + 2 * a2 * u + 3 * a3 * u^2%% f(0) = 0, f'(0) = 0% f(1) = 1, f'(1) = 0%% a0 = 0% a0 + a1 = 0 => a1 = 0%% a0 + a1 + a2 + a3 = 1     => a2 + a3 = 1          => 2 * a2 + 2 * a3 = 2% a1 + 2 * a2 + 3 * a3 = 0  =>                         2 * a2 + 3 * a3 = 0%% a3 = -2% a2 = 1 - a3 = 3%% f(u) = 3 * u^2 - 2 * u^3 = (3 - 2 * u) * u^2